__NUXT_JSONP__("/blog/articles/lamp", (function(a,b,c,d,e,f,g,h,i,j,k,l,m,n,o,p,q,r,s,t,u,v,w,x,y,z){return {data:[{article:{title:t,description:"A walk-through on how to configure Linux, Apache, MariaDB, and PHP (LAMP) for development or production purposes.",img:"markus-spiske-466ENaLuhLY-unsplash.jpg",alt:t,featured:0,author:{name:"Jesse Quinn",bio:"All about Jesse",img:"https:\u002F\u002Fimages.unsplash.com\u002Fphoto-1533636721434-0e2d61030955?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=2550&q=80",alt:"profile photo"},publishedAt:"2019-10-28T03:00:00.000Z",updateAt:r,toc:[],body:{type:"root",children:[{type:b,tag:u,props:{id:"introduction"},children:[{type:b,tag:k,props:{href:"#introduction",ariaHidden:l,tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:"Introduction"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"A LAMP configuration is a linux distro setup to use Apache, a well known and popular httpd, a SQL server such as MariaDB or Mysql, and a scripting language like PHP, Python or Perl; thus forming the acronym LAMP. However, a LAMP server could in fact use Nginx or another httpd server or Postresql or many other databases. "}]},{type:a,value:c},{type:b,tag:u,props:{id:"installation"},children:[{type:b,tag:k,props:{href:"#installation",ariaHidden:l,tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:"Installation"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"The following is based on "},{type:b,tag:k,props:{href:"https:\u002F\u002Fwww.ostechnix.com\u002Finstall-apache-mariadb-php-lamp-stack-on-arch-linux-2016\u002F",rel:[v,w,x],target:y},children:[{type:a,value:"here"}]},{type:a,value:s}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"On Manjaro\u002FArchlinux it is always advisable to run:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo pacman -Syu\nsudo pacman -S apache php php-apache mysql\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"to update your system with current software and install the required packages. You should be prompted with two database options. Select  "},{type:b,tag:e,props:{},children:[{type:a,value:z}]},{type:a,value:s}]},{type:a,value:c},{type:b,tag:q,props:{id:"apache"},children:[{type:b,tag:k,props:{href:"#apache",ariaHidden:l,tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:"Apache"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Edit the Apache configuration file and comment out the following line if it is not already:\n:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fetc\u002Fhttpd\u002Fconf\u002Fhttpd.conf\n# LoadModule unique_id_module modules\u002Fmod_unique_id.so\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Enable and start the Apache service with systemd:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo systemctl enable httpd\nsudo systemctl start httpd\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Verify the status:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo systemctl status httpd\nsudo journalctl -xe\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Create a test index.html with the following html:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fsrv\u002Fhttp\u002Findex.html\n\u003Chtml\u003E\n\u003Chead\u003E\n    \u003Ctitle\u003EHello World\u003C\u002Ftitle\u003E\n\u003C\u002Fhead\u003E\n\u003Cbody\u003E\n    \u003Ch2\u003EHello World\u003C\u002Fh2\u003E\n\u003C\u002Fbody\u003E\n\u003C\u002Fhtml\u003E\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Visit your localhost in your favourite browser http:\u002F\u002Flocalhost or http:\u002F\u002Flocalipaddress to confirm you receive "},{type:b,tag:e,props:{},children:[{type:a,value:"Hello World"}]},{type:a,value:s}]},{type:a,value:c},{type:b,tag:q,props:{id:z},children:[{type:b,tag:k,props:{href:"#mariadb",ariaHidden:l,tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:"MariaDB"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Initialize the database with:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo mysql_install_db --user=mysql --basedir=\u002Fusr --datadir=\u002Fvar\u002Flib\u002Fmysql\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"like Apache we need to use systemd to load the MariaDB service permanently"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo systemctl enable mysqld\nsudo systemctl start mysqld\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Check the status of the service"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo systemctl status mysqld\nsudo journalctl -xe\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"run the following script to setup the root user"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo mysql_secure_installation\n"}]}]}]},{type:a,value:c},{type:b,tag:q,props:{id:"php"},children:[{type:b,tag:k,props:{href:"#php",ariaHidden:l,tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:"PHP"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Revisit the Apache configuration file:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fetc\u002Fhttpd\u002Fconf\u002Fhttpd.conf\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"and comment out the "},{type:b,tag:e,props:{},children:[{type:a,value:"mod_mpm_event"}]},{type:a,value:" and either add or uncomment the following:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"#LoadModule mpm_event_module modules\u002Fmod_mpm_event.so\nLoadModule mpm_prefork_module modules\u002Fmod_mpm_prefork.so\nLoadModule php7_module modules\u002Flibphp7.so\nAddHandler php7-script php\nInclude conf\u002Fextra\u002Fphp7_module.conf\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Create a test for php:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fsrv\u002Fhttp\u002Ftest.php\n\u003C?php\n    phpinfo();\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Restart Apache and vist http:\u002F\u002Flocalhost\u002Ftest.php"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo systemctl restart httpd\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"You are done. You can either use phpMyAdmin or your IDE such as phpstorm\u002Fdatagrip from "},{type:b,tag:k,props:{href:"https:\u002F\u002Fwww.jetbrains.com\u002F",rel:[v,w,x],target:y},children:[{type:a,value:"JetBrains"}]},{type:a,value:" to interact with your database. I personally opt to use my IDE."}]},{type:a,value:c},{type:b,tag:q,props:{id:"phpmyadmin"},children:[{type:b,tag:k,props:{href:"#phpmyadmin",ariaHidden:l,tabIndex:m},children:[{type:b,tag:n,props:{className:[o,p]},children:[]}]},{type:a,value:"phpMyAdmin"}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Install phpMyAdmin:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo pacman -S phpmyadmin\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"Edit the "},{type:b,tag:e,props:{},children:[{type:a,value:"php.ini"}]},{type:a,value:" file and uncomment the following:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fetc\u002Fphp\u002Fphp.ini\nextension=bz2.so\nextension=mysqli.so\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"We need to map phpMyAdmin to an url with Apache:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fetc\u002Fhttpd\u002Fconf\u002Fextra\u002Fphpmyadmin.conf\nAlias \u002Fphpmyadmin \"\u002Fusr\u002Fshare\u002Fwebapps\u002FphpMyAdmin\"\n\u003CDirectory \"\u002Fusr\u002Fshare\u002Fwebapps\u002FphpMyAdmin\"\u003E\n    DirectoryIndex index.php\n    AllowOverride All\n    Options FollowSymlinks\n    Require all granted\n\u003C\u002FDirectory\u003E\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"and include the configuration to the Apache configuration file:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fetc\u002Fhttpd\u002Fconf\u002Fhttpd.conf\nInclude conf\u002Fextra\u002Fphpmyadmin.conf\n"}]}]}]},{type:a,value:c},{type:b,tag:d,props:{},children:[{type:a,value:"We need to also edit the phpMyAdmin configuration file and restart the Apache service:"}]},{type:a,value:c},{type:b,tag:f,props:{className:[g]},children:[{type:b,tag:h,props:{className:[i,j]},children:[{type:b,tag:e,props:{},children:[{type:a,value:"sudo nano \u002Fetc\u002Fwebapps\u002Fphpmyadmin\u002Fconfig.inc.php\n$cfg['blowfish_secret'] = '`somesecrethere`';\nsudo systemctl restart httpd\n"}]}]}]}]},dir:"\u002Farticles",path:"\u002Farticles\u002Flamp",extension:".md",slug:"lamp",createdAt:"2020-07-22T00:27:09.716Z",updatedAt:"2020-07-22T00:30:14.083Z"},prev:{title:"I See the Truth Now!!! Imgur API",updateAt:r,slug:"imgur"},next:{title:"Working with Linters, Fixers, Hooks for Github, etc.",updateAt:r,slug:"linters"}}],fetch:[],mutations:[]}}("text","element","\n","p","code","div","nuxt-content-highlight","pre","language-text","line-numbers","a","true",-1,"span","icon","icon-link","h5","2020-07-19T03:00:00.000Z",".","LAMP Installation on Manjaro","h4","nofollow","noopener","noreferrer","_blank","mariadb")));